mapper.select:
SELECT

FROM SE_RULES


        and SR_ID = #{srId,jdbcType=INTEGER}


        and  SD_TYPE = #{sdType,jdbcType=VARCHAR}


        and  SD_OS = #{sdOs,jdbcType=VARCHAR}


        and  SD_OSTYPE = #{sdOstype,jdbcType=VARCHAR}


        and  SR_TYPE = #{srType,jdbcType=INTEGER}


        and  SR_RNAME = #{srRname,jdbcType=VARCHAR}


        and SR_RULES = #{srRules,jdbcType=INTEGER}


        and SR_DATE = #{srDate,jdbcType=TIMESTAMP}


ORDER BY SR_DATE DESC

SELECT

FROM SE_RULES


        and SR_ID = #{srId,jdbcType=INTEGER}


        and SD_TYPE = #{sdType,jdbcType=VARCHAR}


        and SD_OS = #{sdOs,jdbcType=VARCHAR}


        and SD_OSTYPE = #{sdOstype,jdbcType=VARCHAR}


        and SR_TYPE = #{srType,jdbcType=INTEGER}


        and SR_RNAME = #{srRname,jdbcType=VARCHAR}


        and  SR_RULES = #{srRules,jdbcType=INTEGER}


        and SR_DATE = #{srDate,jdbcType=TIMESTAMP}


ORDER BY SR_ID DESC
limit ${start},${end}

